<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');class Layout {    private $data = array();    public function view($path, $content_data = NULL, $arrJs = array()) {        $CI = & get_instance();        if ($content_data == NULL) {            $this->data["content"] = $CI->load->view($path, "", TRUE);        } else {            $this->data["content"] = $CI->load->view($path, $content_data, TRUE);        }        $this->data["arrJs"] = $arrJs;        return $CI->load->view("_layout/_layout", $this->data);    }		public function ticket($path, $content_data = NULL, $arrJs = array()) {        $CI = & get_instance();        if ($content_data == NULL) {            $this->data["content"] = $CI->load->view($path, "", TRUE);        } else {            $this->data["content"] = $CI->load->view($path, $content_data, TRUE);        }        $this->data["arrJs"] = $arrJs;        return $CI->load->view("layout/_layout", $this->data);    }	public function tlead($path, $content_data = NULL, $arrJs = array()) {        $CI = & get_instance();        if ($content_data == NULL) {            $this->data["content"] = $CI->load->view($path, "", TRUE);        } else {            $this->data["content"] = $CI->load->view($path, $content_data, TRUE);        }        $this->data["arrJs"] = $arrJs;        return $CI->load->view("tllayout/_layout", $this->data);    }		public function staff($path, $content_data = NULL, $arrJs = array()) {        $CI = & get_instance();        if ($content_data == NULL) {            $this->data["content"] = $CI->load->view($path, "", TRUE);        } else {            $this->data["content"] = $CI->load->view($path, $content_data, TRUE);        }        $this->data["arrJs"] = $arrJs;        return $CI->load->view("stafflayout/_layout", $this->data);    }}?> 